import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-navbar',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent {
  isMenuOpen = false;

  toggleMenu(): void {
    this.isMenuOpen = !this.isMenuOpen;
  }

  closeMenu(): void {
    this.isMenuOpen = false;
  }
}
<nav class="navbar navbar-expand-lg navbar-dark bg-gradient sticky-top shadow-sm">
  <div class="container">
    <a class="navbar-brand fw-bold" routerLink="/">
      <i class="bi bi-hearts"></i> Kids Care
    </a>
    <button
      class="navbar-toggler"
      type="button"
      (click)="toggleMenu()"
      [attr.aria-expanded]="isMenuOpen">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" [class.show]="isMenuOpen">
      <ul class="navbar-nav ms-auto">
        <li class="nav-item">
          <a class="nav-link" href="#services" (click)="closeMenu()">Services</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#gallery" (click)="closeMenu()">Gallery</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#contact" (click)="closeMenu()">Contact</a>
        </li>
      </ul>
    </div>
  </div>
</nav>
